mapper.update:
update dish
name = #{dish.name}, category_id = #{dish.categoryId}, price = #{dish.price}, image = #{dish.image}, description = #{dish.description}, status = #{dish.status}, update_time = #{dish.updateTime}, update_user = #{dish.updateUser},
where id = #{dish.id}

mapper.select:
select d.*, c.name as categoryName from dish d left outer join category c on d.category_id = c.id
and d.name like concat('%',#{name},'%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.create_time desc
